mapper.insert:

	insert into tbl_goods (
			gdsNum
			, gdsName
			, cateCode
			, gdsPrice
			, gdsStock
			, gdsDes
			, gdsImg
			, gdsThumbImg
		)
   		values (
	   		#{gdsNum}
	   		, #{gdsName}
	   		, #{cateCode} 
	   		, #{gdsPrice}
	   		, #{gdsStock}
	   		, #{gdsDes}
	   		, #{gdsImg}
	   		, #{gdsThumbImg}
   		)

mapper.select:
SELECT COUNT(1) CNT FROM ( SELECT B.gdsNum
FROM tbl_goods B where B.gdsNum>=0 ORDER BY B.gdsNum DESC
) B

mapper.insert:
update tbl_goods 
set 
	gdsNum=#{gdsNum}
	, gdsName=#{gdsName}
	, cateCode=#{cateCode}
	, gdsPrice=#{gdsPrice}
	, gdsStock=#{gdsStock}
	, gdsDes=#{gdsDes}
	, gdsImg=#{gdsImg}
	, gdsThumbImg=#{gdsThumbImg}
where gdsNum=#{gdsNum}
