mapper.insert:
		SELECT COALESCE(MAX(rno),0)+1 AS rno FROM tbl_reply

	insert into tbl_reply (
			rno
			, bno
			, writer
			, content
			, regDate
		)
   		values (
	   		#{rno}
	   		, #{bno}
	   		, #{writer} 
	   		, #{content}
	   		, NOW()
   		)

mapper.update:
update tbl_reply 
set 
	gdsNum=#{content}
where rno=#{rno} and bno=#{bno} and writer=#{writer}
