mapper.insert:

	SELECT COALESCE(MAX(gdsNum),0)+1 AS gdsNum FROM tbl_goods

	insert into tbl_goods (
			gdsNum
			, gdsName
			, cateCode
			, gdsPrice
			, gdsStock
			, gdsDes
			, gdsImg
			, gdsThumbImg
		)
   		values (
	   		#{gdsNum}
	   		, #{gdsName}
	   		, #{cateCode} 
	   		, #{gdsPrice}
	   		, #{gdsStock}
	   		, #{gdsDes}
	   		, #{gdsImg}
	   		, #{gdsThumbImg}
   		)
