mapper.insert:

	insert into tbl_goods (
			gdsNum
			, gdsName
			, cateCode1
			, cateCode2
			, gdsPrice
			, gdsStock
			, gdsDes
			, gdsImg
			, gdsThumbImg
		)
   		values (
	   		#{gdsNum}
	   		, #{gdsName}
	   		, #{cateCode1} 
	   		, #{cateCode2} 
	   		, #{gdsPrice}
	   		, #{gdsStock}
	   		, #{gdsDes}
	   		, #{gdsImg}
	   		, #{gdsThumbImg}
   		)

mapper.select:
SELECT COUNT(1) CNT FROM ( SELECT B.gdsNum
FROM tbl_goods B where B.gdsNum>=0 ORDER BY B.gdsNum DESC
) B

mapper.insert:
update tbl_goods 
set 
	gdsNum=#{gdsNum}
	, gdsName=#{gdsName}
	, cateCode1=#{cateCode1}
	, cateCode2=#{cateCode2}
	, gdsPrice=#{gdsPrice}
	, gdsStock=#{gdsStock}
	, gdsDes=#{gdsDes}
	, gdsImg=#{gdsImg}
	, gdsThumbImg=#{gdsThumbImg}
where gdsNum=#{no}

mapper.select:
SELECT COUNT(1) CNT FROM ( SELECT B.CID
FROM COMMON_TBL B where B.CID>=0 ORDER BY B.CID DESC
) B

mapper.insert:

  		INSERT INTO COMMON_TBL(
	   	  CID
				, L_CATEGORY
				, S_CATEGORY
				, NAME
				, DESCRPT
		) VALUES(
			#{CID}
		   		, #{L_CATEGORY}
		   		, #{S_CATEGORY}
		   		, #{NAME}
		   		, #{DESCRPT}
		)
		ON DUPLICATE KEY UPDATE
		CID = #{CID}
		, L_CATEGORY = #{L_CATEGORY, jdbcType=VARCHAR}
		, S_CATEGORY = #{S_CATEGORY, jdbcType=VARCHAR}
		, NAME = #{NAME, jdbcType=VARCHAR}
		, DESCRPT = #{DESCRPT}

mapper.update:
 	update COMMON_TBL set
 		NAME=#{NAME}
 		, DESCRPT=#{DESCRPT}
 		, L_CATEGORY=#{L_CATEGORY}
 		, S_CATEGORY=#{S_CATEGORY}
		where CID=#{CID}

update tbl_order
set delivery=#{delivery}
where orderId=#{orderId}
